import { useState } from "react";
import CharacterCard from "../components/CharacterCard";
import ProfileStats from "../components/ProfileStats";
import Achievements from "../components/Achievements";
import Inventory from "../components/Inventory"; // Nov√Ω komponent
import styles from "./Profile.module.css"; // Import CSS modulu

const Profile = () => {
  const [activeTab, setActiveTab] = useState("stats");

  return (
    <div className={styles.profileContainer}>
      <h2>Profil hr√°ƒça</h2>

      <CharacterCard />

      {/* Navigaƒçn√© tlaƒçidl√° pre taby */}
      <div className={styles.profileTabs}>
        <button 
          className={activeTab === "stats" ? styles.active : ""}
          onClick={() => setActiveTab("stats")}
        >
          üìä ≈†tatistiky
        </button>

        <button 
          className={activeTab === "achievements" ? styles.active : ""}
          onClick={() => setActiveTab("achievements")}
        >
          üèÜ Achievementy
        </button>
        <button className={activeTab === "inventory" ? "active" : ""} onClick={() => setActiveTab("inventory")}>
          üéí Invent√°r
        </button>
      </div>

      {/* Obsah akt√≠vnej karty */}
      <div className={styles.profileContent}>
        {activeTab === "stats" && <ProfileStats />}
        {activeTab === "achievements" && <Achievements />}
        {activeTab === "inventory" && <Inventory />}
      </div>
    </div>
  );
};

export default Profile;
